import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchTreatmentPlan } from '../utils/api'; // Ensure this utility is implemented

function TreatmentPlan() {
  const { submissionId } = useParams(); // Fetch submissionId from URL params
  const [treatmentPlan, setTreatmentPlan] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    async function loadTreatmentPlan() {
      try {
        const data = await fetchTreatmentPlan(submissionId);
        setTreatmentPlan(data);
      } catch (err) {
        setError('Failed to load treatment plan. Please try again.');
        console.error('Error fetching treatment plan:', err);
      }
    }
    loadTreatmentPlan();
  }, [submissionId]);

  if (error) {
    return <p className="error-message">{error}</p>;
  }

  if (!treatmentPlan) {
    return <p>Loading treatment plan...</p>;
  }

  return (
    <div className="treatment-plan">
      <h2>Treatment Plan for Submission ID: {submissionId}</h2>

      {treatmentPlan.map((organ) => (
        <div key={organ.organ_name} className="treatment-plan-card">
          <h3>{organ.organ_name}</h3>

          {/* Food Section */}
          <div className="section">
            <h4>Food Recommendations</h4>
            {organ.foods.length > 0 ? (
              organ.foods.map((food, index) => (
                <div key={index} className="item">
                  <strong>{food.food_name}</strong>: {food.properties}
                </div>
              ))
            ) : (
              <p>No food recommendations available.</p>
            )}
          </div>

          {/* Herbs Section */}
          <div className="section">
            <h4>Herbal Remedies</h4>
            {organ.herbs.length > 0 ? (
              organ.herbs.map((herb, index) => (
                <div key={index} className="item">
                  <strong>{herb.herb_name}</strong>: {herb.properties}
                </div>
              ))
            ) : (
              <p>No herbal remedies available.</p>
            )}
          </div>

          {/* Emotions Section */}
          <div className="section">
            <h4>Lifestyle & Emotions</h4>
            {organ.emotions.length > 0 ? (
              organ.emotions.map((emotion, index) => (
                <div key={index} className="item">
                  <strong>{emotion.emotion_name}</strong>: {emotion.description}
                </div>
              ))
            ) : (
              <p>No emotional or lifestyle recommendations available.</p>
            )}
          </div>
        </div>
      ))}
    </div>
  );
}

export default TreatmentPlan;
