import React, { useState, useEffect, useContext } from 'react';
import { fetchQuestions, submitAnswers } from '../utils/api'; // Ensure these functions align with your backend
import { useNavigate } from 'react-router-dom'; // For redirecting after submission
import UserContext from '../context/UserContext'; // Import UserContext

function Survey() {
  const [questions, setQuestions] = useState([]);
  const [responses, setResponses] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { user } = useContext(UserContext); // Access user from context

  useEffect(() => {
    async function loadQuestions() {
      try {
        console.log('Fetching questions...');
        const data = await fetchQuestions();
        console.log('Fetched questions:', data);
        setQuestions(data);
        setResponses(Array(data.length).fill(false)); // Initialize responses with false values
        console.log('Initialized responses state:', Array(data.length).fill(false));
      } catch (err) {
        setError('Failed to load questions. Please try again later.');
        console.error('Error fetching questions:', err);
      }
    }
    loadQuestions();
  }, []);

  const handleResponseChange = (index, value) => {
    console.log(`Changing response for question at index ${index} to ${value}`);
    const updatedResponses = [...responses];
    updatedResponses[index] = value;
    setResponses(updatedResponses);
    console.log('Updated responses state:', updatedResponses);
  };

  const handleSubmit = async () => {
    try {
      if (!user?.id) {
        setError('User not authenticated. Please log in.');
        return;
      }

      console.log('Preparing answers for submission...');
      const answers = questions.map((q, index) => ({
        bitPosition: q.bit_position,
        isTrue: responses[index],
      }));
      console.log('Prepared answers:', answers);

      const submissionId = await submitAnswers(user.id, answers); // Ensure this returns a primitive value
      console.log('Submission successful, Submission ID:', submissionId);

      // Navigate to treatment plan with submissionId
      navigate(`/treatment-plan/${submissionId}`);
    } catch (err) {
      setError('Failed to submit survey. Please try again later.');
      console.error('Error submitting survey:', err);
    }
  };


  return (
    <div className="survey">
      <h2>New Survey</h2>
      {error && <p className="error-message">{error}</p>}
      <form>
        {questions.map((q, index) => (
          <div key={q.id}>
            <label>{q.question_text}</label>
            <input
              type="checkbox"
              checked={responses[index]}
              onChange={(e) => handleResponseChange(index, e.target.checked)}
            />
          </div>
        ))}
      </form>
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
}

export default Survey;
