import React, { useContext, useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import UserContext from '../context/UserContext';

function Dashboard() {
  const { user } = useContext(UserContext); // Access user from context
  const navigate = useNavigate();
  const [lastSubmissionId, setLastSubmissionId] = useState(null); // State to track submission ID

  useEffect(() => {
    // Retrieve the last submission ID from localStorage
    const storedSubmissionId = localStorage.getItem('lastSubmissionId');
    setLastSubmissionId(storedSubmissionId);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('user');
    localStorage.removeItem('lastSubmissionId');
    window.location.reload(); // Reload the app to trigger re-login
  };

  const handleSurveyRedirect = () => {
    navigate('/new-survey');
  };

  return (
    <div className="dashboard">
      <h1>Welcome, {user?.username || 'User'}</h1>
      <p>Manage your surveys and results below.</p>
      <nav>
        <ul>
          <li>
            <button onClick={handleSurveyRedirect}>Start a New Survey</button>
          </li>
          {lastSubmissionId ? (
            <>
              <li>
                <Link to={`/treatment-plan/${lastSubmissionId}`}>View Treatment Plan</Link>
              </li>
              <li>
                <Link to={`/current-diagnosis/${lastSubmissionId}`}>View Current Diagnosis</Link>
              </li>
            </>
          ) : (
            <p>No recent submission found. Complete a survey to see results.</p>
          )}
        </ul>
      </nav>
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
}

export default Dashboard;
